changeSet.sql:
CREATE TABLE IF NOT EXISTS DividendMaster( id serial primary key , symbol VARCHAR(50), name VARCHAR(100), dividendyear INTEGER, quarter INTEGER, dividendamount decimal );
CREATE TABLE IF NOT EXISTS AssetMaster ( id serial PRIMARY KEY, assetName VARCHAR(100), amount decimal, recordDate DATE );
CREATE TABLE IF NOT EXISTS StockMaster ( id serial PRIMARY KEY, exchange VARCHAR(20), symbol VARCHAR(50), name VARCHAR(100), isinNumber VARCHAR(50), faceValue decimal, listingDate DATE, price decimal, stockKey VARCHAR(50), CONSTRAINT UC_ExchangeSymbol UNIQUE (exchange,symbol) );
CREATE TABLE IF NOT EXISTS StockPriceMaster ( id serial PRIMARY KEY, stockMasterId INTEGER, stockKey VARCHAR(50), price decimal, priceTime timestamp, timeZoneName VARCHAR(20) );
